import React, { useState} from "react";
import Scanner from "./components/Scanner";
import { gql, useMutation } from "@apollo/client";
import "./styles.css";   

export default function List (){

      const [camera, setCamera] = useState(false);
      const [result, setResult] = useState(null);

      const onDetected = result => {
        setResult(result);
      };


      function AddRecord(){
        const ADD_INPUT = gql`
        mutation($name: String!, $price: Int!, $barcode: String!) {
          addInput(name: $name, price: $price, barcode: $barcode) {
            response

          }
        }
      `;

  const [addUser, { data, loading }] = useMutation(ADD_INPUT);

        const [inputName, setinputName ] =useState({name:"",price:0,barcode:result} );
//        const [inputPrice, setinputPrice ] =useState(NaN);
        return(<>
        <input type="text" onChange={(e)=>setinputName({name:e.target.value,price:inputName.price,barcode:result})} placeholder="Name"></input>
        <input type="number" onChange={(e)=>setinputName({name:inputName.name,price:e.trget.inputPrice,barcode:result})} placeholder="Price"></input>
        <input type="submit" onClick={()=>addUser({ variables: inputName })}></input>

        </>)
      }

      return (
        <div className="App">
          <p>{result ? result : "Scanning..."}</p>

          <button onClick={() => setCamera(!camera)}>
            {camera ? "Stop" : "Start"}
          </button>
          <div className="container">
            {camera && <Scanner onDetected={onDetected} />}
          </div>
          <AddRecord/>
        </div>
      );
    }
