import { useQuery, gql,useMutation } from "@apollo/client";


function Delete(props){
  console.log(props);
  const DELETE_ITEM = gql`
  mutation($idd: String!) {
    deleteitem(idd: $idd) {

      status
    }
  }
`;


const [deletionStatus, { data, loading }] = useMutation(DELETE_ITEM);


console.log( data)
  return (

    <>
    <input type="button" onClick={()=>deletionStatus(({ variables: props }))} value="Delete"></input>
    </>
  )
}




const RECORDS = gql`
  query{records{nome
  id
  price
  barcode
  }}
`;

export default function Records() {
  const { loading, error, data } = useQuery(RECORDS);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  if(data )console.log(data);
  return data.records.map((res) => (
    <p key={res.id}>
      <Delete idd={res.id}/>nome:{res.nome} 
        id:{res.id}
      Price:{res.price} Barcode:{res.barcode}
    </p>
  ));
}
