import React, { useState } from "react";
import Scanner from "./components/Scanner";
import ReactDOM from "react-dom";
import "./styles.css";   

export default function List (){

      const [camera, setCamera] = useState(false);
      const [result, setResult] = useState(null);
      const [inputName, setinputName ] =useState("");
      const [inputPrice, setinputPrice ] =useState(NaN);

      const onDetected = result => {
        setResult(result);
      };
      function AddRecord(){

        return(<>
        <input type="text" onChange={(e)=>setinputName.target.value}></input>
        <input type="number" onChange={(e)=>setinputPrice.target.value}></input>
        <input type="submit" onClick={()=>console.log(inputName)}></input>

        </>)
      }

      return (
        <div className="App">
          <p>{result ? result : "Scanning..."}</p>
          <p>{result?AddRecord:{}}</p>
          <button onClick={() => setCamera(!camera)}>
            {camera ? "Stop" : "Start"}
          </button>
          <div className="container">
            {camera && <Scanner onDetected={onDetected} />}
          </div>
        </div>
      );
    }
