    moreData: "aaarraaayy",
    children: [101, 102],
  },
  {
    id: 106,
    name: "Objectspec2",
    moreData: "ooobbbjjject",
    children: [101, 102],
  },
];
function buttonNameFromArray(id) {
  let obj = dat.find((s) => s.id === id);
  console.log(obj,"objbutton");
  return <input type="button" value={obj.name} key={obj.id} onClick={()=>CreateRender(id=obj.id)}></input>;
}
