import React, { useState, useEffect } from "react";
import { gql, useMutation, useLazyQuery } from "@apollo/client";
export default function Dato() {
    function Funct(prop) {
        console.log(prop, "prop");
        return (
          <>
            <div>
              <p>Name:{prop.data.dato.name}</p>
              <p>Description:{prop.data.dato.Description}</p>
              <p>Info:{prop.data.dato.info}</p>
              <p>father{prop.data.dato.father}</p>

              {prop.data.dato.child.map((x, index) => (
                <>
                  <input
                    type="button"
                    key={index}
                    value={x.ID}
                    onClick={() => pro(x.ID)}
                  ></input>
                  <br></br>
                </>
              ))}
            </div>
          </>
        );
      }
    function IdToRender(prop) {
        console.log(prop,"propssssss")
        const DATO = gql`
          query dato($id: String) {
            dato(id: $id) {
              name
              info
              Description
              father
              child {
                ID
                Name
              }
            }
          }
        `;
        const [getFields, { loading, data }] = useLazyQuery(DATO);
        useEffect(() => {
          getFields({ variables: { id: prop.id } });

          if (data) {
            console.log(data);
            setfunctionToRender(<Funct data={data} />);
          }
        }, [data, getFields]);
        return <>{functionToRender}</>;
      }




}
