import { React, useState } from "react";
import { gql, useMutation } from "@apollo/client";

const ADD_USER = gql`
  mutation($name: String!, $username: String!, $email: String!) {
    addUser(name: $name, username: $username, email: $email) {
      id
      name
      username
      email
    }
  }
`;

export default function Mut() {
  const [addUser, { data, loading }] = useMutation(ADD_USER);
  const [user, setUser] = useState({name: "",
  username: "",
  email: "",});
console.log(user)
  return (
    <>
      <input type="text" onChange={(e) => setUser(e.target.value)}placeholder="nome"></input>
      <input type="text" onChange={(e)=>{setUser()}}place></input>
      <input type="password" onChange={(e)=>{setUser()}}></input>
      <input type="button"

        placeholder="submit"
        onClick={() => {
          addUser({
            variables: {

              name: "daniel",
              username: "pigos",
              email: "pss",
            },
          });
        }}
      ></input>

      <input
        type="button"
        onClick={() => console.log(data.addUser, "data", loading, "x")}
      ></input>
      {loading ? <p>loading</p> : <p>done</p>}
    </>
  );
}
