import React, { useState, useEffect} from "react";
import { gql, useLazyQuery } from "@apollo/client";

export default function CRYPTO() {
const [result,setresult]=useState({name:"",value:[]})
const [callQuery,setcallQuery]=useState(false)
function RenderResults(){

    return (<><p>name:  {result.name} </p><ul>value:{result.value.map((x,index)=>(<li key={index}>{x}</li>))}</ul></>)
}


  const CRYPTO = gql`
  query crypto($symb: String) {
    crypto(symb: $symb) {
      name
      value
    }
  }
`;
const [getFields, { loading, data }] = useLazyQuery(CRYPTO);
 const [inputsUser, setinputsUser] = useState({
    symbol: undefined,

  });
//   if(callQuery){
//     setInterval(function intervalRef(){
//         getFields({ variables: { symb: inputsUser.symbol } })

//     }, 5000)}
  useEffect(() => {




    if(data){
        console.log(data)
        setresult({name:data.crypto.name,value:[...result.value,data.crypto.value]})
      }  

      const interval = setInterval(() => {getFields({ variables: { symb: inputsUser.symbol } })
      }, 1000);
      return () => clearInterval(interval);
  },[data,callQuery])

//   useEffect(() => {
//     const interval = setInterval(() => {
//       console.log('This will run every second!');
//     }, 1000);
//     return () => clearInterval(interval);
//   }, []);
 return (
     <>
     <input type="text" onChange={(e)=>setinputsUser({symbol:e.target.value})}></input>
     <input type="submit" onClick={()=>setcallQuery(true)}></input>
     <input type="submit" onClick={()=>console.log('')}></input>
     <RenderResults/>
     </>
 )}
