import React, { useState, useEffect } from "react";
import { gql, useMutation, useQuery, useLazyQuery } from "@apollo/client";
export default function Home() {
  //const [createRender, setcreaterender] = useState(<CreateRender id="101" />);
  const [inputsUser, setinputsUser] = useState({
    id: "36",
    name: "",
    description: "",
    extraInfo: "",
    child: [],
  });
  const [renderQuery, setrenderQuery] = useState();
  const [renderDataLoading, setrenderDataLoading] = useState(<></>);

  const DATO = gql`
    query dato($id: String) {
      dato(id: $id) {
        name
        info
        Description
      }
    }
  `;
  const { loading1, data1 } = useQuery(DATO,{ variables: { id: inputsUser.id } });
  const [getFields, { loading, data }] = useLazyQuery(DATO);

  useEffect(() => {
    if (loading1) {
      setrenderDataLoading(
        <>
          <p>loading</p>
        </>
      );
    } else {
      setrenderDataLoading(<></>);
    }
    if (loading) {
      setrenderDataLoading(
        <>
          <p>loading</p>
        </>
      );
    } else {
      setrenderDataLoading(<></>);
    }
    if (data1) {
      console.log(data1.dato);
      setrenderDataLoading(
        <div>
          <p>Name:{data1.dato.name}</p>
          <p>Info:{data1.dato.info}</p>
          <p>Description:{data1.dato.Description}</p>
        </div>
      );
    }

    if (data) {
      console.log(data.dato);
      setrenderDataLoading(
        <div>
          <p>Name:{data.dato.name}</p>
          <p>Info:{data.dato.info}</p>
          <p>Description:{data.dato.Description}</p>
        </div>
      );
    }
  }, [loading, data, loading1, data1]);

  return (
    <>
      <div>
        <input
          type="text"
          onChange={(e) =>
            setinputsUser({ ...inputsUser, name: e.target.value })
          }
          placeholder="Name"
        ></input>
        <input
          type="text"
          onChange={(e) =>
            setinputsUser({ ...inputsUser, description: e.target.value })
          }
          placeholder="Descriptio"
        ></input>
        <input
          type="text"
          onChange={(e) =>
            setinputsUser({ ...inputsUser, extraInfo: e.target.value })
          }
          placeholder="ExtraInfo"
        ></input>
      </div>
      <input
        type="button"
        onClick={() => getFields({ variables: { id: inputsUser.id } })}
        value="submit"
      ></input>

      <input
        type="button"
        onClick={() => console.log(data1)}
        value="submitt"
      ></input>
      {renderDataLoading}
      {renderQuery}
    </>
  );
}
