import React, { useState, useEffect,Component } from "react";
import { gql, useMutation,  useLazyQuery } from "@apollo/client";
import { Line } from 'react-chartjs-2';

export default function CRYPTO() {
    const [labels,setlabels]=useState([1,2,3,4,5,6,7,9]);

    const [valueCrypto,setvalueCrypto]=useState([0.10,0.20,0.5,0.4,0.5,0.6,0.7,0.9]);

    const [period,setperiod]=useState({
        labels: labels,
        datasets: [
          {
            label: 'My First dataset',
            fill: false,
            lineTension: 0.1,
            backgroundColor: 'rgba(75,192,192,0.4)',
            borderColor: 'rgba(75,192,192,1)',
            borderCapStyle: 'butt',
            borderDash: [],
            borderDashOffset: 0.0,
            borderJoinStyle: 'miter',
            pointBorderColor: 'rgba(75,192,192,1)',
            pointBackgroundColor: '#fff',
            pointBorderWidth: 1,
            pointHoverRadius: 5,
            pointHoverBackgroundColor: 'rgba(75,192,192,1)',
            pointHoverBorderColor: 'rgba(220,220,220,1)',
            pointHoverBorderWidth: 2,
            pointRadius: 1,
            pointHitRadius: 10,
            data: valueCrypto
          }
        ]
      })
class LineDemo extends Component {
    render() {
      return (
        <div>
          <h2>Chart</h2>
          <Line ref="chart" data={period} />
        </div>
      );
    }

  }
  const CRYPTO = gql`
  query crypto($symb: String) {
    crypto(symb: $symb) {
      name
      value
    }
  }
`;
const [getFields, { loading, data }] = useLazyQuery(CRYPTO);
 const [inputsUser, setinputsUser] = useState({
    symbol: undefined,

  });
  if(data){

  }

 return (
     <>
     <input type="text" onChange={(e)=>setinputsUser({symbol:e.target.value})}></input>
     <input type="submit" onClick={()=>getFields({ variables: { symb: inputsUser.symbol } })}></input>
     <input type="submit" onClick={()=> {setlabels(labels=>[...labels,10]);setvalueCrypto(valueCrypto=>[...valueCrypto,0.1])}}></input>
     <LineDemo/>
     </>
 )}
