import { React, useState } from "react";
import { gql, useMutation } from "@apollo/client";

const ADD_USER = gql`
  mutation($id: ID, $name: String!, $username: String!, $email: String!) {
    addUser(id: $id, name: $name, username: $username, email: $email) {
      id
      name
      username
      email
    }
  }
`;

export default function Mut() {
  const [addUser, { data, loading }] = useMutation(ADD_USER);
  const [username, setusername] = useState("");

  return (
    <>
      <input type="text" onChange={(e) => setusername(e.target.value)}></input>
      <input
        type="button"

        placeholder="submit"
        onClick={() => {
          addUser({
            variables: {
              id: "1",
              name: "daniel",
              username: "pigos",
              email: "pss",
            },
          });
        }}
      ></input>
      <div>{username}</div>
      <input
        type="button"
        onClick={() => console.log(data.addUser, "data", loading, "x")}
      ></input>
      {loading ? <p>loading</p> : <p>done</p>}
    </>
  );
}
